  | salign = right
  | source = Maya Angelou, 1984<ref>{{cite book |last = Angelou |first = Maya |chapter = Shades and Slashes of Light |editor1-last = Evans |editor1-first = Mari |title = Black Women Writers (1950–1980): A Critical Evaluation |publisher = Doubleday |year = 1984 |location = Garden City, New York |page = [https://archive.org/details/blackwomenwriter00evan/page/5 5] |isbn = 978-0-385-17124-3 |chapter-url = https://archive.org/details/blackwomenwriter00evan/page/5 }}</ref>
